INSERT INTO rooms VALUES(1,'Stugan','Du vaknar upp i en mörk och kuslig stuga. Du ser en dörr i nordlig riktning som otroligt nog verkar vara öppen. I stugan ser du också en stor bultsax och en flaska med okänt innehåll.',2,NULL,NULL,NULL,'img/rum1.png',NULL,NULL,NULL,NULL);
INSERT INTO rooms VALUES(2,'Trädgården','Du hamnar i en mörk trädgård. Det verkar vara ett högt staket runt hela trädgården och omöjligt att ta sig igenom åt alla håll, förutom öst där det finns en stor grind eller tillbaka till stugan. Grinden är låst med en grov kedja och omöjlig att klättra över. I trädgården ser du även en spade och en trädgårdstomte där någonting glänser till under den.',NULL,3,1,NULL,'img/rum2.png',NULL,'Bultsax',NULL,NULL);
INSERT INTO rooms VALUES(3,'Gläntan','Efter att ha tagit dig igenom grinden hamnar du i en skogsglänta. Framför dig finns en skattkista som är låst. Du ser ut att behöva en nyckel för att öppna den.',NULL,NULL,NULL,2,NULL,NULL,NULL,NULL,NULL);
